    @Override
    public void onUpgrade(SQLiteDatabase db, int i, int i1) {
        db.execSQL("DROP TABLE IF EXISTS city");
        onCreate(db);
    }

    public String getCityNameByLatitudeLong(double lat,double lon){
        SQLiteDatabase db = this.getReadableDatabase();
        String city="not found";



        String query="SELECT `city_id`,`name`,`latitude`,`longitude` from city";

        double distance=1000000000;
        Cursor res =  db.rawQuery( query, null );
        while (res.moveToNext()) {

            double distanceCal=distance(lat,lon,res.getDouble(2),res.getDouble(3));
            if(distanceCal<distance){
                distance=distanceCal;
                city=res.getString(1);
            }

        }
        return city;
    }
    private double distance(double lat1, double lon1, double lat2, double lon2) {
        double theta = lon1 - lon2;
        double dist = Math.sin(deg2rad(lat1))
                * Math.sin(deg2rad(lat2))
                + Math.cos(deg2rad(lat1))
                * Math.cos(deg2rad(lat2))
                * Math.cos(deg2rad(theta));
        dist = Math.acos(dist);
        dist = rad2deg(dist);
        dist = dist * 60 * 1.1515;
        return (dist);
    }

    private double deg2rad(double deg) {
        return (deg * Math.PI / 180.0);
    }

    private double rad2deg(double rad) {
        return (rad * 180.0 / Math.PI);
    }
